//scss
import './ModalEmployeeCreated.scss'

//React
import { useEffect } from 'react';

function Modal({ isOpen, handleClose, title, content, className }) {
  useEffect(() => {
    const body = document.body;
    body.classList.toggle('no-scroll', isOpen);
    return () => body.classList.remove('no-scroll');
  }, [isOpen]);

  if (!isOpen) return null;

  return (
    <div className={`modal ${className || ''}`}>
      <div className="modal__content">
        <span className="modal__content__close" onClick={handleClose}>X</span>
        {title && <h2 className="modal__content__title">{title}</h2>}
        <div className="modal__content__body">{content}</div>
      </div>
    </div>
  );
}

export default Modal
