//scss
import './Modal.scss'

//React
import React, { useEffect, useRef } from 'react';


// Icon Cross close par défault sur la modal
const defaultCloseIcon = (
  <svg viewBox="0 0 24 24" width="18" height="18" stroke="currentColor" strokeWidth="2" fill="none" strokeLinecap="round" strokeLinejoin="round">
    <line x1="18" y1="6" x2="6" y2="18"></line>
    <line x1="6" y1="6" x2="18" y2="18"></line>
  </svg>
);

function Modal({ isOpen, handleClose, title, content, disableScroll = true, closeIcon = defaultCloseIcon, autoSize = false }) {

  const modalRef = useRef(null);



  // logique pour empecher de scroller lorsque la modal est ouverte
  useEffect(() => {
    if (disableScroll) {
      const body = document.body;
      body.classList.toggle('no-scroll', isOpen);

      return () => body.classList.remove('no-scroll');
    }
  }, [isOpen, disableScroll]);

  // logique pour fermer la modale avec ESC
  useEffect(() => {
    function handleKeyDown(event) {
      if (event.key === 'Escape') {
        handleClose();
      }
    }

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleClose]);

  // logique pour fermer la modale avec click exterieur modal
  useEffect(() => {
    function handleClickOutside(event) {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        handleClose();
      }
    }
    // Ajoute l'écouteur d'événements au document
    document.addEventListener('mousedown', handleClickOutside);

    // Nettoie l'écouteur d'événements lors du démontage du composant
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [handleClose]); // S'assure que le handler ne change pas

  useEffect(() => {
    if (autoSize && isOpen) {
      const modalContentHeight = modalContentRef.current.offsetHeight;
      modalRef.current.style.height = `${modalContentHeight}px`;
    }
  }, [isOpen, autoSize]);

  return isOpen ? (
    <div className={`wrapperModal`} >
      <div className="wrapperModal__modal" ref={modalRef}>

        <span className="wrapperModal__modal__iconClose" onClick={handleClose}>
        {closeIcon}
        </span>

        {title && <h2 className="wrapperModal__modal__title">
          {title}
        </h2>}

        <div className="wrapperModal__modal__content" >
          {content}
          {/* Lien pour fermer la modal */}
          <a href="#" onClick={handleClose} className="wrapperModal__modal__linkClose" >Fermer la modal</a>
        </div>

      </div>
    </div>
  ) : null;
}

export default Modal
