//scss
import './Modal.scss'

//React
import React, { useEffect, useRef, useState } from 'react';


// Icon Cross close par défault sur la modal
const defaultCloseIcon = (
  <svg viewBox="0 0 24 24" width="18" height="18" stroke="currentColor" strokeWidth="2" fill="none" strokeLinecap="round" strokeLinejoin="round">
    <line x1="18" y1="6" x2="6" y2="18"></line>
    <line x1="6" y1="6" x2="18" y2="18"></line>
  </svg>
);

function Modal({ 
  isOpen, 
  title, 
  content, 
  handleClose, 
  showCloseLink = true, 
  escapeClose = true,
  closeOnClickOutside = true,
  enableCloseIconClick = true,
  disableScroll = true, 
  closeIcon = defaultCloseIcon,
  centeredModal = true,
  transitionFast,
  transitionMedium,
  transitionSlow 

}) {
  //ref pour accéder au DOm de la modal
  const modalRef = useRef(null);

  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    let timer;
    if (isOpen) {
      setIsVisible(true); // Rendre la modale visible immédiatement pour l'ouverture
    } else {
      // Ne pas changer l'état isVisible ici pour éviter la transition de fermeture
    }
    return () => clearTimeout(timer); // Nettoyer le timer si le composant est démonté
  }, [isOpen]);


  let transitionStyle = {};
  if (transitionFast) {
    transitionStyle = { transition: 'opacity 100ms ease', opacity: 1 };
  } else if (transitionMedium) {
    transitionStyle = { transition: 'opacity 1000ms ease', opacity: 1 };
  } else if (transitionSlow) {
    transitionStyle = { transition: 'opacity 1000ms ease', opacity: 1 };
  }

  useEffect(() => {
    if (isOpen) {
      const timer = setTimeout(() => setIsVisible(true), 10); // Un délai minimal pour s'assurer que la transition s'applique
      return () => clearTimeout(timer);
    }
  }, [isOpen]);

  // Styles de transition appliqués à la modale
  const modalStyle = {
    ...transitionStyle,
    display: isVisible ? 'block' : 'none',
    opacity: isVisible ? 1 : 0, 
  };



  // logique pour empecher de scroller lorsque la modal est ouverte
  useEffect(() => {
    if (disableScroll) {
      const body = document.body;
      body.classList.toggle('no-scroll', isOpen);

      return () => body.classList.remove('no-scroll');
    }
  }, [isOpen, disableScroll]);

  // logique pour fermer la modale avec ESC
  useEffect(() => {
    function handleKeyDown(event) {
      if (event.key === 'Escape' && escapeClose)  {
        handleClose();
      }
    }

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleClose, escapeClose]);

  // logique pour fermer la modale avec click exterieur modal
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (closeOnClickOutside && modalRef.current && !modalRef.current.contains(event.target)) {
        handleClose();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [handleClose, closeOnClickOutside]); 

  // logique pour autoriser l'utilisation de close icon click
  const handleIconClick = () => {
    if (enableCloseIconClick) {
      handleClose();
    }
  };

  return shouldRender ? (
    <div 
      className={`modal ${centeredModal ? 'centeredModal' : ''}`}
      ref={modalRef}
      style={modalStyle}
    >
      <span className="modal__iconClose" onClick={handleIconClick}>
      {closeIcon}
      </span>
      {title && <h2 className="modal__title">
        {title}
      </h2>}
      <div className="modal__content" >
        {content}
        {showCloseLink && (
        <a href="#" onClick={handleClose} className="modal__linkClose">
          Fermer la modal
        </a>
      )}
      </div>
    </div>

  ) : null;
}

export default Modal
